import React from 'react';
import {Form, Input, Cascader, Select, Checkbox, Button } from "antd";
const {Option} = Select;

const LoginForm = () => {
  const [form] = Form.useForm();

  const onFinish = () => {};
  return (
    <Form form={form} name="login" onFinish={onFinish} style={{ textAlign: 'center'}}>
      <Form.Item name="email" label="E-mail" style={{ width:"400px" }}
      rules={[
        {type: "email",
        message:"The input is not valid E-mail",
        },
        {
          required: true,
          message:"Please input your E-mail",
        }
      ]}
      >
        <Input />
      </Form.Item>
      <Form.Item name="password" label="Password" style={{ width:"400px", textAlign: 'center' }}
      rules={[
      {
        required: true,
        message:"Please input your password",
      }]}>
      
        <Input.Password />
      </Form.Item>
      <Form.Item>
        <Button type="primary" htmlType="submit">
          Login
        </Button>
      </Form.Item>
    </Form>
  );
};

export default LoginForm;
